sp_header_footer.client_script:
api.controller = function() {
  /* Widget controller */
  var c = this;

  // Obtém a hora atual
  var currentHour = new Date().getHours();

  // Define a saudação com base na hora do dia
  c.greeting = currentHour < 12 ? 'Bom dia!' : 'Boa tarde!';
};

